// Entrada: items con { asignado_a, estado, count }
const norm = s => String(s || '').replace(/\s+/g,' ').trim();

const byEmp = new Map();
for (const it of $input.all()) {
  const emp = norm(it.json.asignado_a);
  const estado = String(it.json.estado || '');
  const count = Number(it.json.count || 0);
  if (!byEmp.has(emp)) byEmp.set(emp, { executed:0, inprogress:0, onhold:0, specified:0 });

  const acc = byEmp.get(emp);
  if (estado === 'Closed') acc.executed += count;
  else if (estado === 'In progress') acc.inprogress += count;
  else if (estado === 'On hold') acc.onhold += count;
  else if (estado === 'Backlog Approbed') acc.specified += count;
}

// Devuelve un bloque de 4 UPDATEs por empleado
const out = [];
for (const [emp, v] of byEmp.entries()) {
  const sql =
    `UPDATE personal_cost SET task_executed = ${v.executed} WHERE employee = '${emp}';\n` +
    `UPDATE personal_cost SET task_inprogress = ${v.inprogress} WHERE employee = '${emp}';\n` +
    `UPDATE personal_cost SET task_onhold = ${v.onhold} WHERE employee = '${emp}';\n` +
    `UPDATE personal_cost SET task_specified = ${v.specified} WHERE employee = '${emp}';`;
  out.push({ json: { output: sql } });
}
return out;

// Entrada: items con { output } o { query } (bloque con varias sentencias)
// Salida: 1 item por sentencia UPDATE en { query }

const items = $input.all();
const out = [];

for (const it of items) {
  // Toma el campo correcto y coerciona SIEMPRE a string
  const rawField = (it?.json?.output ?? it?.json?.query ?? '');
  const raw = (rawField ?? '').toString();

  // Si estÃ¡ vacÃ­o, salta
  if (!raw.trim()) continue;

  // Limpia fences y normaliza saltos de lÃ­nea
  const cleaned = raw
    .replace(/^\s*```(?:sql)?\s*/i, '')
    .replace(/\s*```\s*$/i, '')
    .replace(/\r\n/g, '\n')
    .trim();

  // Divide por ';' cuando sigue otra sentencia SQL o fin
  const stmts = cleaned
    .split(/;\s*(?=(?:UPDATE|INSERT|DELETE|SELECT)\b|$)/gi)
    .map(s => (s ?? '').toString().trim())
    .filter(s => s.length > 0)
    .map(s => (s.endsWith(';') ? s : s + ';'));

  // QuÃ©date solo con UPDATE (si quieres ejecutar sÃ³lo esos)
  for (const q of stmts) {
    if (!/^UPDATE\b/i.test(q)) continue;
    // Rompe el pairedItem para evitar errores aguas abajo
    out.push({ json: { query: q }, pairedItem: [] });
  }
}

return out;
