  'Cta. Ingreso del producto',
  'Cta. Gastos del producto',
  'Cta. Inventario',
  'Cta. Costo del producto',
];

// Â¿Deseas considerar vacÃ­o como error?
const TREAT_EMPTY_AS_ERROR = false;

// Â¿ComparaciÃ³n sin may/minÃºsculas?
const UPPERCASE_MATCH = false;

// --- Helpers de normalizaciÃ³n (todo a string y limpio) ---
const toStr = (v) => {
  if (v === null || v === undefined) return '';
  if (typeof v === 'string') return v;
  if (typeof v === 'number' || typeof v === 'boolean') return String(v);
  try { return JSON.stringify(v); } catch { return String(v); }
};

const clean = (s) => {
  let out = toStr(s);
  out = out.replace(/\u00A0/g, ' ');                 // NBSP -> espacio normal
  out = out.replace(/[\u200B-\u200D\uFEFF]/g, '');   // zero-width + BOM
  out = out.replace(/[\r\n\t]+/g, ' ');              // quita saltos/tabs
  if (out.normalize) out = out.normalize('NFKC');    // normaliza unicode
  out = out.trim().replace(/\s+/g, ' ');             // colapsa espacios
  if (UPPERCASE_MATCH) out = out.toUpperCase();
  return out;
};

// 1) HOJA: solo 4 columnas (strings limpios) + _row
const hoja = ($items('Categorias3') || []).map((it, i) => {
  const row = it.json || {};
  const out = { _row: row.row_number ?? (i + 2) };   // asume encabezado en fila 1
  for (const c of COLS) out[c] = clean(row[c]);
  return out;
});

// 2) POSTGRES: array de strings (columna value), limpios + SET para bÃºsqueda O(1)
const postgres = ($items('Catalogo de Cuentas OB1') || [])
  .map(it => it?.json?.value ?? it?.json?.VALUE ?? it?.json?.Value)
  .map(clean)
  .filter(v => v !== '');
const postgresSet = new Set(postgres);

// 3) Comparar cada fila/columna contra postgresSet
const faltantes = [];
const hojaVal = hoja.map(r => {
  const missingCols = [];
  for (const col of COLS) {
    const val = r[col]; // ya limpio
    if (val === '' && !TREAT_EMPTY_AS_ERROR) continue;
    if (!postgresSet.has(val)) {
      missingCols.push({ columna: col, valor: val });
      faltantes.push({ fila: r._row, columna: col, valor: val });
    }
  }
  return { ...r, esValido: missingCols.length === 0, faltantes: missingCols };
});

// 4) Mensaje global
const mensaje = faltantes.length === 0
  ? 'CATEGORIAS - âœ… Todo Correcto'
  : 'CATEGORIAS - ðŸš« Errores encontrados:\n- ' + faltantes
      .map(e => `Fila ${e.fila} - Columna "${e.columna}" valor *${e.valor}* no existe en catÃ¡logo`)
      .join('\n- ');

return [{ json: { mensaje } }];

