// Lee todo lo que llega del Merge y decide EXITO / ERROR
function pickMsgs(items) {
  return items.map(i => String(i.json?.mensaje ?? '').trim()).filter(Boolean);
}
function hasErr(s) { return /error/i.test(s); }
function bullets(msg) {
  const m = String(msg).match(/errores?\s+encontrados:\s*([\s\S]*)/i);
  const body = (m ? m[1] : msg).replace(/\r/g, '');
  const set = new Set(body.split('\n').map(x => x.trim()).filter(Boolean));
  return Array.from(set).map(x => x.startsWith('-') ? x : `- ${x}`);
}

const msgs = pickMsgs($items('Merge2'));
const errs = msgs.filter(hasErr);

let resultado, mensaje;
if (errs.length === 0) {
  resultado = 'EXITO';
  mensaje = 'CATEGORIAS - âœ… Todo Correcto';
} else {
  const all = errs.flatMap(bullets);
  resultado = 'ERROR';
  mensaje = `CATEGORIAS - ðŸš« Errores encontrados:\n${all.join('\n')}`;
}

return [{ json: { resultado, mensaje, totalMensajes: msgs.length, totalConError: errs.length } }];

