// === ConfiguraciÃ³n ===
const NORMALIZAR = true;   // true: sin tildes, MAYÃšSCULAS y espacios colapsados
const TRIM_EMPTY = true;   // true: ignora vacÃ­os

// Normalizadores
const cleanDisplay = (s) => {
  let out = (s ?? '').toString();
  out = out.replace(/\u00A0/g, ' ');
  out = out.replace(/[\u200B-\u200D\uFEFF]/g, '');
  out = out.replace(/[\r\n\t]+/g, ' ');
  if (out.normalize) out = out.normalize('NFKC');
  return out.trim().replace(/\s+/g, ' ');
};
const norm = (s) => {
  let out = cleanDisplay(s);
  if (TRIM_EMPTY && out === '') return '';
  if (!NORMALIZAR) return out;
  return out
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '') // sin tildes
    .toUpperCase();
};

// === 1) Datasheet (Google Sheets: nodo 'Categorias3') ===
// Debe traer columnas 'Codigo' y 'Nombre'
const sheetRows = ($items('Categorias3') || []).map((it, i) => {
  const r = it.json || {};
  return {
    rowNumber: r.row_number ?? (i + 2), // encabezado en fila 1
    CodigoRaw: cleanDisplay(r.Codigo),
    CodigoNorm: norm(r.Codigo),
    NombreRaw: cleanDisplay(r.Nombre),
    NombreNorm: norm(r.Nombre),
  };
});

// === 2) CatÃ¡logo existente en OB (Postgres: nodo 'Catalogo de Categorias OB') ===
// Query: select value, name from m_product_category
const obRows = ($items('Catalogo de Categorias OB') || []).map(it => it.json || {});
const obCodigos = new Set(obRows.map(r => norm(r.value)).filter(v => v !== ''));
const obNombres = new Set(obRows.map(r => norm(r.name)).filter(v => v !== ''));

// === 3) Acumular coincidencias (sheet que ya existen en OB) ===
// Guardamos por clave normalizada para agrupar filas y mostrar un solo mensaje por valor
const dupCodigo = new Map(); // key -> { muestra, rows[] }
const dupNombre = new Map();

for (const r of sheetRows) {
  // CÃ³digo ya existente en OB
  if (r.CodigoNorm) {
    if (obCodigos.has(r.CodigoNorm)) {
      const e = dupCodigo.get(r.CodigoNorm) || { muestra: r.CodigoRaw || '(vacÃ­o)', rows: [] };
      e.rows.push(r.rowNumber);
      dupCodigo.set(r.CodigoNorm, e);
    }
  }

  // Nombre ya existente en OB
  if (r.NombreNorm) {
    if (obNombres.has(r.NombreNorm)) {
      const e = dupNombre.get(r.NombreNorm) || { muestra: r.NombreRaw || '(vacÃ­o)', rows: [] };
      e.rows.push(r.rowNumber);
      dupNombre.set(r.NombreNorm, e);
    }
  }
}

// === 4) Construir mensajes en el formato solicitado ===
// "Duplicado en columna "Nombre": valor "EXTRA" en filas 2, 3"
const errores = [];

// Ordenar por valor para salida estable
const sortByMuestra = (a, b) => a.muestra.localeCompare(b.muestra, 'es');

[...dupCodigo.values()].sort(sortByMuestra).forEach(e => {
  e.rows.sort((a, b) => a - b);
  errores.push(`Duplicado en columna "Codigo": valor "${e.muestra}" en filas ${e.rows.join(', ')}`);
});
[...dupNombre.values()].sort(sortByMuestra).forEach(e => {
  e.rows.sort((a, b) => a - b);
  errores.push(`Duplicado en columna "Nombre": valor "${e.muestra}" en filas ${e.rows.join(', ')}`);
});

// === 5) Salida ===
const mensaje = errores.length === 0
  ? 'CATEGORIAS (existentes en OB) - âœ… No se encontraron CÃ³digos/Nombre presentes en OB'
  : 'CATEGORIAS (existentes en OB) - ðŸš« Error Coincidencias encontradas:\n- ' + errores.join('\n- ');

return [{
  json: {
    mensaje,
    totalErrores: errores.length
  }
}];

