// Clasifica: 'texto' | 'imagen' | 'audio' | 'video' | 'documento'
// Mantiene SIEMPRE el mismo formato de retorno:
// { kind, text, media, raw, sender, timestamp }

function unwrap(m) {
  if (!m) return m;
  if (m.ephemeralMessage?.message) return unwrap(m.ephemeralMessage.message);
  if (m.viewOnceMessage?.message) return unwrap(m.viewOnceMessage.message);
  if (m.viewOnceMessageV2?.message) return unwrap(m.viewOnceMessageV2.message);
  return m;
}

function toNum(x) {
  const n = Number(x);
  return Number.isFinite(n) ? n : null;
}

function isImageMime(mt) {
  return typeof mt === "string" && mt.toLowerCase().startsWith("image/");
}

function isImageFilename(name) {
  if (!name) return false;
  return /\.(jpe?g|png|webp|gif|bmp|tif?f)$/i.test(String(name));
}

function detectar(body) {
  const msg = unwrap(body?.message || {});
  if (!msg) return { kind: "desconocido", text: "", media: null, raw: body };

  // === AUDIO ===
  if (msg.audioMessage) {
    const a = msg.audioMessage;
    const url = a.url || "";
    return {
      kind: "audio",
      text: "",
      media: {
        url,
        mimetype: a.mimetype || null,
        encrypted: url.includes(".enc"),
        mediaKey: a.mediaKey || null,
        directPath: a.directPath || null,
        seconds: typeof a.seconds === "number" ? a.seconds : toNum(a.seconds),
        ptt: Boolean(a.ptt)
      },
      raw: body
    };
  }

  // === VIDEO ===
  if (msg.videoMessage) {
    const v = msg.videoMessage;
    const url = v.url || "";
    return {
      kind: "video",
      text: v.caption || "",
      media: {
        url,
        mimetype: v.mimetype || null,
        encrypted: url.includes(".enc"),
        mediaKey: v.mediaKey || null,
        directPath: v.directPath || null,
        seconds: typeof v.seconds === "number" ? v.seconds : toNum(v.seconds),
        gifPlayback: Boolean(v.gifPlayback)
      },
      raw: body
    };
  }

  // === STICKER (tratar como IMAGEN) ===
  if (msg.stickerMessage) {
    const s = msg.stickerMessage;
    const url = s.url || "";
    return {
      kind: "imagen",
      text: "",
      media: {
        url,
        mimetype: s.mimetype || "image/webp",
        encrypted: url.includes(".enc"),
        mediaKey: s.mediaKey || null,
        directPath: s.directPath || null,
        isSticker: true,
        isAnimated: Boolean(s.isAnimated)
      },
      raw: body
    };
  }

  // === IMAGEN ===
  if (msg.imageMessage) {
    const i = msg.imageMessage;
    const url = i.url || "";
    return {
      kind: "imagen",
      text: i.caption || "",
      media: {
        url,
        mimetype: i.mimetype || null,
        encrypted: url.includes(".enc"),
        mediaKey: i.mediaKey || null,
        directPath: i.directPath || null
      },
      raw: body
    };
  }

  // === DOCUMENTO (si es imagen -> clasificar como IMAGEN; si no, DOCUMENTO) ===
  if (msg.documentMessage) {
    const d = msg.documentMessage;
    const url = d.url || "";
    const fileName = d.fileName || d.title || null;
    const mime = d.mimetype || null;

    if (isImageMime(mime) || isImageFilename(fileName)) {
      // document enviado como imagen -> tratar como imagen
      return {
        kind: "imagen",
        text: "",
        media: {
          url,
          mimetype: mime || "image/jpeg",
          encrypted: url.includes(".enc"),
          mediaKey: d.mediaKey || null,
          directPath: d.directPath || null,
          fromDocument: true,
          fileName,
          fileLength: toNum(d.fileLength),
          fileSha256: d.fileSha256 || null,
          fileEncSha256: d.fileEncSha256 || null,
          mediaKeyTimestamp: toNum(d.mediaKeyTimestamp)
        },
        raw: body
      };
    }

    // documento "normal"
    return {
      kind: "documento",
      text: "",
      media: {
        url,
        mimetype: mime,
        encrypted: url.includes(".enc"),
        mediaKey: d.mediaKey || null,
        directPath: d.directPath || null,
        fileName,
        fileLength: toNum(d.fileLength),
        pageCount: toNum(d.pageCount),
        fileSha256: d.fileSha256 || null,
        fileEncSha256: d.fileEncSha256 || null,
        mediaKeyTimestamp: toNum(d.mediaKeyTimestamp),
        // miniatura (si existe)
        thumbnail: {
          jpegThumbnail: d.jpegThumbnail || null,
          thumbnailDirectPath: d.thumbnailDirectPath || null,
          thumbnailSha256: d.thumbnailSha256 || null,
          thumbnailEncSha256: d.thumbnailEncSha256 || null,
          width: toNum(d.thumbnailWidth),
          height: toNum(d.thumbnailHeight)
        }
      },
      raw: body
    };
  }

  // === TEXTO ===
  if (msg.conversation || msg.extendedTextMessage?.text) {
    return {
      kind: "texto",
      text: msg.conversation || msg.extendedTextMessage?.text || "",
      media: null,
      raw: body
    };
  }

  // Desconocido
  return { kind: "desconocido", text: "", media: null, raw: body };
}

