    const len = val.length;

    // Longitud (solo aplica cuando hay regla definida)
    if (maxLen[col] !== undefined && len > maxLen[col]) {
      errores.push(`Columna "${col}" en fila ${rowNumber} (${len} > ${maxLen[col]})`);
    }

    // Saltos de lÃ­nea (incluye separadores Unicode)
    if (newlineRegex.test(valRaw)) {
